["level0101009"]={id="level0101009",bodyPos="-1",offset={{0,0},{0,0},{0,0}},modelldScale={60,60,60},bodyAniList={},expression="",npcImageColor="",content_langId="她的声音放低了一些。",voiceId="",bgmId="",voiceEffectId={},talkBg="",effect="",},
