["level205006"]={id="level205006",bodyPos="2=1161,-115",offset={{0,0},{0,-150},{0,0}},modelldScale={60,70,60},bodyAniList={},expression="",npcImageColor="",content_langId="阿桃！",voiceId="",bgmId="",voiceEffectId={},talkBg="",effect="",},
["level205007"]={id="level205007",bodyPos="-1",offset={{0,0},{0,-150},{0,0}},modelldScale={60,70,60},bodyAniList={},expression="",npcImageColor="",content_langId="重鹤微微抬高了声音，都阿桃愣了愣神，不自觉地低下头。",voiceId="",bgmId="",voiceEffectId={},talkBg="",effect="",},
["level205008"]={id="level205008",bodyPos="2=1161,-115",offset={{0,0},{0,-150},{0,0}},modelldScale={60,70,60},bodyAniList={},expression="",npcImageColor="",content_langId="许诗葵虽是灵草化形，但以至纯清气救人性命，会重伤她的根本。这是玉叶谷最后的底牌，尚不可滥用！",voiceId="",bgmId="",voiceEffectId={},talkBg="",effect="",},
["level205009"]={id="level205009",bodyPos="1=-115,1161",offset={{0,0},{0,-150},{0,0}},modelldScale={60,70,60},bodyAniList={},expression="115_look,0,0",npcImageColor="",content_langId="哥哥，不是这样的。阿葵是我们的朋友，并不是……",voiceId="",bgmId="",voiceEffectId={},talkBg="",effect="",},
["level205010"]={id="level205010",bodyPos="1=-115,1161",offset={{0,0},{0,-150},{0,0}},modelldScale={60,70,60},bodyAniList={},expression="115_look,0,0",npcImageColor="",content_langId="至少，至少……",voiceId="",bgmId="",voiceEffectId={},talkBg="",effect="",},
