["level205012"]={id="level205012",bodyPos="-1",offset={{0,0},{0,-150},{0,0}},modelldScale={60,70,60},bodyAniList={},expression="",npcImageColor="",content_langId="一阵令人心悸的沉默之后，重鹤终究躬身，行以一礼。",voiceId="",bgmId="",voiceEffectId={},talkBg="",effect="",},
["level205013"]={id="level205013",bodyPos="2=-1161,-115",offset={{0,0},{0,-150},{0,0}},modelldScale={60,70,60},bodyAniList={},expression="0,116_close,0",npcImageColor="",content_langId="……谷主有令，重鹤自无不从。",voiceId="",bgmId="",voiceEffectId={},talkBg="",effect="",},
