["level205015"]={id="level205015",bodyPos="0=100",offset={{0,0},{0,-150},{0,0}},modelldScale={60,70,60},bodyAniList={},expression="",npcImageColor="",content_langId="玉叶谷今日相助，铸剑阁绝无不报之理。",voiceId="",bgmId="",voiceEffectId={},talkBg="",effect="8,1,playerName",},
["level205016"]={id="level205016",bodyPos="3=115",offset={{0,0},{0,-150},{0,0}},modelldScale={60,70,60},bodyAniList={},expression="",npcImageColor="",content_langId="再睡一会儿吧，苒苒，我们马上就出发……",voiceId="",bgmId="",voiceEffectId={},talkBg="",effect="",},
["level205017"]={id="level205017",bodyPos="3=115",offset={{0,0},{0,-150},{0,0}},modelldScale={60,70,60},bodyAniList={},expression="",npcImageColor="",content_langId="就像在玉叶谷那样，我和阿葵，都会等着你醒来！",voiceId="",bgmId="",voiceEffectId={},talkBg="",effect="",},
