["level307014"]={id="level307014",bodyPos="3=101",offset={{0,0},{0,0},{0,0}},modelldScale={60,60,60},bodyAniList={},expression="",npcImageColor="",content_langId="阁主，您说了什么…？",voiceId="",bgmId="",voiceEffectId={},talkBg="",effect="",},
